import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import NewspaperLayout from "./layouts/NewspaperLayout";

const BookAd = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const { newspaperId, newspaperName, spaceId } = location.state || {};

  const [selectedNewspaper, setSelectedNewspaper] = useState(newspaperName || "");
  const [adContent, setAdContent] = useState("");
  const [calculatedPrice, setCalculatedPrice] = useState(0);

  // Handle newspaper selection
  const handleNewspaperChange = (event) => {
    setSelectedNewspaper(event.target.value);
  };

  // Calculate price based on ad content
  const calculatePrice = () => {
    if (!selectedNewspaper) {
      alert("Please select a newspaper first.");
      return;
    }
    const pricePerWord = selectedNewspaper === "The Virtual Times" ? 10 : 8;
    const wordCount = adContent.trim().split(/\s+/).length;
    const totalPrice = pricePerWord * wordCount;
    setCalculatedPrice(totalPrice);
    alert(`Total Price: ₹${totalPrice}`);
  };

  // Proceed to payment
  const proceedToPayment = () => {
    if (!selectedNewspaper || !adContent.trim()) {
      alert("Please select a newspaper and enter ad content before proceeding.");
      return;
    }
    navigate("/payment", {
      state: {
        newspaperId,
        newspaperName: selectedNewspaper,
        spaceId,
        adContent,
        totalPrice: calculatedPrice,
      },
    });
  };

  return (
    <div style={styles.bookAdContainer}>
      <div style={styles.bookAdBox}>
        <h2 style={styles.heading}>Book Your Advertisement</h2>

        {/* Newspaper Dropdown */}
        <label htmlFor="newspaper" style={styles.label}>
          Select a Newspaper:
        </label>
        <select
          id="newspaper"
          onChange={handleNewspaperChange}
          value={selectedNewspaper}
          style={styles.newspaperSelect}
        >
          <option value="">-- Select --</option>
          <option value="The Virtual Times">The Virtual Times - ₹10 per word</option>
          <option value="Daily News">Daily News - ₹8 per word</option>
        </select>

        {/* Newspaper Layout (Preview) */}
        {selectedNewspaper && (
          <div style={styles.newspaperLayoutContainer}>
            <NewspaperLayout name={selectedNewspaper} />
          </div>
        )}

        {/* Ad Content Input */}
        <label htmlFor="adContent" style={styles.label}>
          Enter Ad Content:
        </label>
        <textarea
          id="adContent"
          placeholder="Type your ad content here..."
          value={adContent}
          onChange={(e) => setAdContent(e.target.value)}
          style={styles.textarea}
        />

        {/* Display Calculated Price */}
        {calculatedPrice > 0 && (
          <p style={styles.priceText}>Total Price: ₹{calculatedPrice}</p>
        )}

        {/* Action Buttons */}
        <div style={styles.buttonContainer}>
          <button style={styles.button} onClick={calculatePrice}>
            Calculate Price
          </button>
          <button style={styles.button} onClick={proceedToPayment}>
            Proceed to Payment
          </button>
        </div>
      </div>
    </div>
  );
};

// Inline CSS Styles (for better alignment and responsiveness)
const styles = {
  bookAdContainer: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    backgroundColor: "#f0f4f8",
    margin: 0,
    padding: "20px",
    boxSizing: "border-box",
  },
  bookAdBox: {
    width: "600px",
    padding: "30px",
    backgroundColor: "#ffffff",
    borderRadius: "12px",
    boxShadow: "0 8px 16px rgba(0, 0, 0, 0.1)",
    display: "flex",
    flexDirection: "column",
    boxSizing: "border-box",
  },
  heading: {
    fontSize: "28px",
    color: "#2c3e50",
    textAlign: "center",
    marginBottom: "20px",
  },
  label: {
    fontWeight: "bold",
    marginBottom: "10px",
    textAlign: "left",
  },
  newspaperSelect: {
    margin: "10px 0 20px",
    padding: "12px",
    width: "60%",
    border: "1px solid #ccc",
    borderRadius: "8px",
    boxSizing: "border-box",
  },
  newspaperLayoutContainer: {
    marginBottom: "20px",
  },
  textarea: {
    margin: "10px 0",
    padding: "12px",
    width: "100%",
    height: "120px",
    border: "1px solid #ccc",
    borderRadius: "8px",
    resize: "none",
    boxSizing: "border-box",
  },
  priceText: {
    fontSize: "18px",
    color: "#34495e",
    margin: "20px 0",
    textAlign: "center",
  },
  buttonContainer: {
    display: "flex",
    justifyContent: "space-between",
    marginTop: "20px",
  },
  button: {
    flex: 1,
    margin: "0 10px",
    padding: "12px 20px",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background 0.3s ease",
    textAlign: "center",
  },
};

export default BookAd;
