import { useState, useEffect } from "react";
import axios from "axios";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [adSpaces, setAdSpaces] = useState([]);
  const [selectedAdSpace, setSelectedAdSpace] = useState(null);
  const [adContent, setAdContent] = useState("");
  const [calculatedPrice, setCalculatedPrice] = useState(null);

  useEffect(() => {
    // Fetch newspapers
    axios
      .get("http://localhost:5000/api/newspapers")
      .then((response) => setNewspapers(response.data))
      .catch((error) => console.error("Error fetching newspapers:", error));
  }, []);

  const handleNewspaperSelect = async (newspaperId) => {
    setSelectedNewspaper(newspaperId);

    try {
      const response = await axios.get(`http://localhost:5000/api/adspaces/${newspaperId}`);
      setAdSpaces(response.data);
    } catch (error) {
      console.error("Error fetching ad spaces:", error);
    }
  };

  const handleAdSpaceSelect = (spaceId, price) => {
    setSelectedAdSpace(spaceId);
    setCalculatedPrice(price); // Base price before content adjustment
  };

  const handleContentChange = (event) => {
    const content = event.target.value;
    setAdContent(content);

    // Calculate price based on content length
    const basePrice = adSpaces.find((space) => space.id === selectedAdSpace)?.price || 0;
    const adjustedPrice = basePrice + content.length * 0.1; // Example: $0.1 per character
    setCalculatedPrice(adjustedPrice.toFixed(2));
  };

  const handleBooking = async () => {
    if (!selectedAdSpace || !adContent.trim()) {
      alert("Please select an ad space and enter ad content.");
      return;
    }

    const bookingData = {
      newspaperId: selectedNewspaper,
      adSpaceId: selectedAdSpace,
      content: adContent,
      price: calculatedPrice,
    };

    try {
      const response = await axios.post("http://localhost:5000/api/book-ad", bookingData);
      console.log("Booking response:", response.data);
      alert("✅ Ad booked successfully!");
    } catch (error) {
      console.error("Error booking ad:", error);
      alert("❌ Booking failed. Please try again.");
    }
  };

  return (
    <div>
      <h2>Book an Ad</h2>

      <label>Select Newspaper:</label>
      <select value={selectedNewspaper || ""} onChange={(e) => handleNewspaperSelect(e.target.value)}>
        <option value="">-- Select --</option>
        {newspapers.map((n) => (
          <option key={n.id} value={n.id}>
            {n.name}
          </option>
        ))}
      </select>

      {selectedNewspaper && (
        <>
          <h3>Available Ad Spaces:</h3>
          {adSpaces.length === 0 ? (
            <p>No ad spaces available for this newspaper.</p>
          ) : (
            <ul>
              {adSpaces.map((space) => (
                <li key={space.id}>
                  <label>
                    <input type="radio" name="adSpace" onChange={() => handleAdSpaceSelect(space.id, space.price)} />
                    {space.space_name} - ${space.price}
                  </label>
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {selectedAdSpace && (
        <>
          <h3>Enter Ad Content:</h3>
          <textarea value={adContent} onChange={handleContentChange} rows="4" cols="50"></textarea>
          <p>Price: ${calculatedPrice}</p>
          <button onClick={handleBooking}>Book Ad</button>
        </>
      )}
    </div>
  );
};

export default AdBooking;
