import React, { useEffect, useState } from "react";
import { getNewspapers } from "../services/api"; // ✅ Import the API function

const BookAd = () => {
  const [newspapers, setNewspapers] = useState([]);

  useEffect(() => {
    const fetchNewspapers = async () => {
      const data = await getNewspapers();
      setNewspapers(data);
    };
    fetchNewspapers();
  }, []);

  return (
    <div>
      <h2>Book an Advertisement</h2>
      <h3>Available Newspapers:</h3>
      {newspapers.length > 0 ? (
        newspapers.map((paper) => (
          <div key={paper.id}>
            <h3>{paper.name} - Base Price: ₹{paper.price_per_word}</h3> {/* ✅ Fixed */}
          </div>
        ))
      ) : (
        <p>No newspapers available.</p>
      )}
    </div>
  );
};

export default BookAd;
