import React, { useState, useContext } from "react";

import { loginUser } from "../services/api";
import { AuthContext } from "../context/AuthContext";
import { FaEye, FaEyeSlash, FaUser, FaLock } from "react-icons/fa";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const { fetchUser } = useContext(AuthContext);
  const navigate = useNavigate();

  const validateForm = () => {
    if (!email || !email.includes("@")) {
      toast.error("Please enter a valid email address");
      return false;
    }
    if (!password || password.length < 6) {
      toast.error("Password must be at least 6 characters");
      return false;
    }
    return true;
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (!validateForm()) return;
    
    setLoading(true);

    try {
      const data = await loginUser(email, password);
      console.log('Login response:', data);
      
      if (!data.token) {
        throw new Error('Authentication failed: No token received');
      }

      // Store authentication data
      const storage = rememberMe ? localStorage : sessionStorage;
      storage.setItem("token", data.token);
      storage.setItem("role", data.user?.role || "user");
      storage.setItem("userName", data.user?.name || "User");
      
      // Update auth context
      await fetchUser();
      
      // Show success message
      toast.success(`Welcome back, ${data.user?.name || 'User'}!`);
  
      // Redirect based on role
      const redirectPath = data.user?.role === "admin" 
        ? "/admin/dashboard" 
        : "/dashboard";
      navigate(redirectPath);

    } catch (error) {
      console.error("Login error:", error);
      const errorMessage = error.response?.data?.message 
        || error.message 
        || "Login failed. Please try again.";
      toast.error(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  // Enhanced styling with loading state and better visual hierarchy
  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      minHeight: "100vh",
      background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
      padding: "20px"
    },
    card: {
      width: "100%",
      maxWidth: "450px",
      backgroundColor: "rgba(255, 255, 255, 0.95)",
      borderRadius: "16px",
      boxShadow: "0 10px 30px rgba(0, 0, 0, 0.2)",
      padding: "2.5rem",
      transition: "all 0.3s ease",
      backdropFilter: "blur(5px)"
    },
    heading: {
      color: "#4a5568",
      fontSize: "2rem",
      fontWeight: "700",
      marginBottom: "2rem",
      textAlign: "center",
      background: "linear-gradient(90deg, #4a5568, #2d3748)",
      WebkitBackgroundClip: "text",
      WebkitTextFillColor: "transparent"
    },
    form: {
      display: "flex",
      flexDirection: "column",
      gap: "1.5rem"
    },
    inputGroup: {
      position: "relative"
    },
    inputIcon: {
      position: "absolute",
      left: "15px",
      top: "50%",
      transform: "translateY(-50%)",
      color: "#718096"
    },
    input: {
      width: "100%",
      padding: "14px 15px 14px 45px",
      borderRadius: "10px",
      border: "1px solid #e2e8f0",
      fontSize: "16px",
      transition: "all 0.2s ease",
      backgroundColor: "#f8fafc"
    },
    inputFocus: {
      borderColor: "#667eea",
      boxShadow: "0 0 0 3px rgba(102, 126, 234, 0.2)",
      outline: "none"
    },
    passwordContainer: {
      position: "relative"
    },
    eyeIcon: {
      position: "absolute",
      right: "15px",
      top: "50%",
      transform: "translateY(-50%)",
      color: "#718096",
      cursor: "pointer",
      transition: "color 0.2s ease"
    },
    eyeIconHover: {
      color: "#667eea"
    },
    rememberMe: {
      display: "flex",
      alignItems: "center",
      gap: "8px",
      marginTop: "-10px"
    },
    button: {
      width: "100%",
      padding: "16px",
      background: "linear-gradient(90deg, #667eea, #764ba2)",
      color: "white",
      border: "none",
      borderRadius: "10px",
      fontSize: "16px",
      fontWeight: "600",
      cursor: "pointer",
      transition: "all 0.3s ease",
      marginTop: "10px",
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      gap: "10px",
      boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)"
    },
    buttonHover: {
      transform: "translateY(-2px)",
      boxShadow: "0 6px 12px rgba(0, 0, 0, 0.15)"
    },
    buttonDisabled: {
      background: "linear-gradient(90deg, #a0aec0, #718096)",
      cursor: "not-allowed",
      transform: "none",
      boxShadow: "none"
    },
    spinner: {
      border: "3px solid rgba(255, 255, 255, 0.3)",
      borderRadius: "50%",
      borderTop: "3px solid white",
      width: "20px",
      height: "20px",
      animation: "spin 1s linear infinite"
    },
    footer: {
      textAlign: "center",
      marginTop: "1.5rem",
      color: "#4a5568"
    },
    link: {
      color: "#667eea",
      fontWeight: "600",
      textDecoration: "none",
      cursor: "pointer"
    }
  };

  return (
    <div style={styles.container}>
      <div 
        style={styles.card}
        onMouseEnter={e => e.currentTarget.style.boxShadow = "0 15px 40px rgba(0, 0, 0, 0.25)"}
        onMouseLeave={e => e.currentTarget.style.boxShadow = "0 10px 30px rgba(0, 0, 0, 0.2)"}
      >
        <h2 style={styles.heading}>Welcome Back</h2>
        
        <form onSubmit={handleLogin} style={styles.form}>
          <div style={styles.inputGroup}>
            <FaUser style={styles.inputIcon} />
            <input
              type="email"
              placeholder="Email Address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={styles.input}
              onFocus={e => e.target.style = {...styles.input, ...styles.inputFocus}}
              onBlur={e => e.target.style = styles.input}
            />
          </div>
          
          <div style={styles.passwordContainer}>
            <FaLock style={styles.inputIcon} />
            <input
              type={showPassword ? "text" : "password"}
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              minLength="6"
              style={styles.input}
              onFocus={e => e.target.style = {...styles.input, ...styles.inputFocus}}
              onBlur={e => e.target.style = styles.input}
            />
            <span 
              onClick={() => setShowPassword(!showPassword)}
              style={styles.eyeIcon}
              onMouseEnter={e => e.currentTarget.style = {...styles.eyeIcon, ...styles.eyeIconHover}}
              onMouseLeave={e => e.currentTarget.style = styles.eyeIcon}
            >
              {showPassword ? <FaEyeSlash /> : <FaEye />}
            </span>
          </div>
          
          <div style={styles.rememberMe}>
            <input
              type="checkbox"
              id="rememberMe"
              checked={rememberMe}
              onChange={(e) => setRememberMe(e.target.checked)}
            />
            <label htmlFor="rememberMe">Remember me</label>
          </div>
          
          <button
            type="submit"
            style={{
              ...styles.button,
              ...(loading && styles.buttonDisabled)
            }}
            disabled={loading}
            onMouseEnter={e => !loading && (e.currentTarget.style = {...styles.button, ...styles.buttonHover})}
            onMouseLeave={e => !loading && (e.currentTarget.style = styles.button)}
          >
            {loading ? (
              <>
                <div style={styles.spinner}></div>
                Authenticating...
              </>
            ) : (
              <>
                <FaLock />
                Login
              </>
            )}
          </button>
        </form>

        <div style={styles.footer}>
          <p>
            Don't have an account?{' '}
            <span 
              style={styles.link}
              onClick={() => navigate("/register")}
            >
              Sign up
            </span>
          </p>
          <p>
            <span 
              style={styles.link}
              onClick={() => navigate("/forgot-password")}
            >
              Forgot password?
            </span>
          </p>
        </div>
      </div>

      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
          body {
            margin: 0;
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
          }
        `}
      </style>
    </div>
  );
};

export default Login;
