import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

// ✅ Get token safely from localStorage
const getToken = () => {
  const token = localStorage.getItem("token");
  return token ? `Bearer ${token}` : ""; // ✅ Ensure token format is correct
};

// ✅ Fetch newspapers
export const getNewspapers = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/newspapers`);
    return response.data; // Ensure it returns an array
  } catch (error) {
    console.error("❌ Fetch Newspapers Error:", error.response?.data || error);
    return [];
  }
};

// ✅ Login API
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/login`, { email, password });

    // ✅ Save token to localStorage after login
    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
    }

    return response.data;
  } catch (error) {
    console.error("❌ Login Error:", error.response?.data || error);
    throw error;
  }
};

// ✅ Signup API
export const signupUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/signup`, userData);
    return response.data;
  } catch (error) {
    console.error("❌ Signup Error:", error.response?.data || error);
    throw error;
  }
};

// ✅ Get Authenticated User (Fix 401 Unauthorized Error)
export const getUserData = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/auth/user`, {
      headers: {
        Authorization: getToken(), // ✅ Fix: Ensure token is sent correctly
        "Content-Type": "application/json",
      },
      withCredentials: true, // ✅ Optional: Needed only if using cookies
    });
    return response.data;
  } catch (error) {
    console.error("❌ User Fetch Error:", error.response?.data || error);
    localStorage.removeItem("token"); // ✅ Remove invalid token if fetch fails
    return null;
  }
};

// ✅ Decode Token to Get User Info (Alternative)
export const getAuthenticatedUser = () => {
  const token = localStorage.getItem("token");
  if (!token) return null;
  try {
    return JSON.parse(atob(token.split(".")[1])); // ✅ Decode JWT Payload
  } catch (error) {
    console.error("❌ Token Decode Error:", error);
    return null;
  }
};
