import React, { useState } from "react";
import "../../styles/NewspaperLayout.css";


const NewspaperLayout = () => {
  const [selectedPage, setSelectedPage] = useState(1);

  return (
    <div className="newspaper-container">
      <div className="newspaper-content">
        <h1 className="newspaper-title">The Times - Advertisement Booking</h1>

        {/* Page Tabs */}
        <div className="page-tabs">
          {[1, 2, 3].map((page) => (
            <button
              key={page}
              className={selectedPage === page ? "active" : ""}
              onClick={() => setSelectedPage(page)}
            >
              Page {page}
            </button>
          ))}
        </div>

        {/* Layout Grid */}
        <div className="newspaper-grid">
          {/* News Column */}
          <div className="news-column">
            <h2>Best Travel Destinations for 2025</h2>
            <p>Plan your next adventure...</p>
            <h2>Upcoming Movie Releases</h2>
            <p>Exciting films coming soon...</p>
          </div>

          {/* Ad Column */}
          <div className="ads-column">
            <div className="ad-space">Business Ad</div>
            <div className="ad-space">Matrimony Ad</div>
            <div className="ad-space large">Travel Ad</div>
            <div className="ad-space">Property Ad</div>
            <div className="ad-space">Vehicle Ad</div>
          </div>
        </div>

        {/* Booking Section */}
        <div className="booking-section">
          <label>Select Advertisement Category:</label>
          <select>
            <option value="business">Business Ad</option>
            <option value="matrimony">Matrimony Ad</option>
            <option value="travel">Travel Ad</option>
            <option value="property">Property Ad</option>
            <option value="vehicle">Vehicle Ad</option>
          </select>
          <button className="book-btn">Book Now</button>
        </div>
      </div>
    </div>
  );
};

export default NewspaperLayout;
