import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const navigate = useNavigate(); // Initialize navigation

  // Ad sizes and prices
  const adDetails = [
    { size: "large", price: 5000, label: "Exclusive Front Page Ad Space" },
    { size: "medium", price: 3000, label: "Premium Half-Page Ad" },
    { size: "small", price: 1500, label: "Quarter-Page Ad" },
    { size: "large", price: 5000, label: "Prime Full-Width Ad Spot" },
  ];

  // Handle Ad Click
  const handleAdClick = (index) => {
    setSelectedAd(index);
  };

  // Navigate to Booking Page with Ad Details
  const handleAdBooking = (size, price) => {
    navigate("/deccan-chronicles-booking", { state: { size, price } });
  };

  return (
    <div className="newspaper-layout">
      <div className="newspaper-container">
        <h1 className="newspaper-title">THE VIRTUAL TIMES</h1>
        <p className="sub-title">Your Trusted Daily Source</p>
        <div className="separator"></div>

        <div className="main-headline">
          <h2>Pakistanâ€™s FIA to Collaborate with CBI on Fighting Terror</h2>
          <p>
            Intense cross-border security discussions are underway to curb
            international crime and ensure stability...
          </p>
        </div>

        {/* Ad Spaces */}
        {adDetails.map((ad, index) => (
          <div
            key={index}
            className={`ad-space ${ad.size}`}
            onClick={() => handleAdClick(index)}
          >
            ðŸ”¹ <strong>{ad.label}</strong> ðŸ”¹
            {selectedAd === index && (
              <div>
                <p className="ad-price">Price: â‚¹{ad.price}</p>
                <button
                  className="book-ad-button"
                  onClick={() => handleAdBooking(ad.size, ad.price)}
                >
                  Book Ad
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default NewspaperLayout;
