import { useState, useEffect } from "react";
import axios from "axios";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [adSpaces, setAdSpaces] = useState([]);
  const [selectedAdSpace, setSelectedAdSpace] = useState(null);
  const [adContent, setAdContent] = useState("");
  const [calculatedPrice, setCalculatedPrice] = useState(0);

  useEffect(() => {
    axios.get("http://localhost:5000/api/newspapers")
      .then(response => setNewspapers(response.data))
      .catch(error => console.error("Error fetching newspapers:", error));
  }, []);

  const handleNewspaperSelect = async (newspaperId) => {
    setSelectedNewspaper(newspaperId);
    try {
      const response = await axios.get(`http://localhost:5000/api/adspaces/${newspaperId}`);
      setAdSpaces(response.data);
    } catch (error) {
      console.error("Error fetching ad spaces:", error);
    }
  };

  const handleAdSpaceSelect = (space) => {
    setSelectedAdSpace(space);
    setCalculatedPrice(space.price);
  };

  const handleContentChange = (e) => {
    setAdContent(e.target.value);
    if (selectedAdSpace) {
      const contentSize = e.target.value.length;
      const newPrice = selectedAdSpace.base_price + (contentSize * 0.1);
      setCalculatedPrice(newPrice);
    }
  };

  const handleBooking = async () => {
    if (!selectedAdSpace) return alert("Please select an ad space.");
    try {
      const response = await axios.post("http://localhost:5000/api/book-ad", {
        newspaper_id: selectedNewspaper,
        ad_space_id: selectedAdSpace.id,
        content: adContent,
        price: calculatedPrice,
      });
      alert("✅ Booking successful!");
    } catch (error) {
      console.error("Booking failed:", error);
      alert("❌ Booking failed. Try again.");
    }
  };

  return (
    <div>
      <h2>Book an Ad</h2>

      <label>Select Newspaper:</label>
      <select value={selectedNewspaper || ""} onChange={(e) => handleNewspaperSelect(e.target.value)}>
        <option value="">-- Select --</option>
        {newspapers.map(n => (
          <option key={n.id} value={n.id}>{n.name}</option>
        ))}
      </select>

      {selectedNewspaper && (
        <>
          <h3>Available Ad Spaces:</h3>
          {adSpaces.length === 0 ? (
            <p>No ad spaces available.</p>
          ) : (
            <ul>
              {adSpaces.map(space => (
                <li key={space.id} onClick={() => handleAdSpaceSelect(space)}>
                  {space.space_name} - ${space.price}
                </li>
              ))}
            </ul>
          )}
        </>
      )}

      {selectedAdSpace && (
        <>
          <h3>Selected Ad Space: {selectedAdSpace.space_name}</h3>
          <label>Enter Ad Content:</label>
          <textarea value={adContent} onChange={handleContentChange} />
          <p>Final Price: ${calculatedPrice.toFixed(2)}</p>
          <button onClick={handleBooking}>Book Ad</button>
        </>
      )}
    </div>
  );
};

export default AdBooking;
