import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles.css";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState("");
  const [adSpaces, setAdSpaces] = useState([]);
  const [selectedAdSpace, setSelectedAdSpace] = useState(null);
  const [adContent, setAdContent] = useState("");
  const [calculatedPrice, setCalculatedPrice] = useState(0);
  const navigate = useNavigate();

  // ✅ Fetch newspapers on component mount
  useEffect(() => {
    axios.get("/api/newspapers")
      .then((res) => setNewspapers(res.data))
      .catch((error) => console.error("Error fetching newspapers:", error));
  }, []);

  // ✅ Handle newspaper selection
  const handleNewspaperSelect = async (e) => {
    const newspaperId = e.target.value;
    setSelectedNewspaper(newspaperId);

    try {
      const res = await axios.get(`/api/ads/adspaces/${newspaperId}`);
      setAdSpaces(res.data);
    } catch (error) {
      console.error("Error fetching ad spaces:", error);
      alert("Failed to load ad spaces. Try again later.");
    }
  };

  // ✅ Handle ad space selection
  const handleAdSpaceSelect = (space) => {
    if (space.status === "booked") return;
    setSelectedAdSpace(space);
  };

  // ✅ Handle ad content input and price calculation
  const handleAdContentChange = (e) => {
    const content = e.target.value;
    setAdContent(content);
    setCalculatedPrice(content.length * 2);
  };

  // ✅ Handle ad booking
  const handleBookAd = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const bookingData = {
      newspaperId: selectedNewspaper,
      adSpaceId: selectedAdSpace.id,
      content: adContent,
      price: calculatedPrice,
    };

    try {
      await axios.post("/api/ads/book", bookingData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Ad booked successfully! Confirmation email sent.");
      navigate("/user-dashboard");
    } catch (error) {
      console.error("Booking failed", error);
      alert("Failed to book ad. Try again.");
    }
  };

  return (
    <div className="ad-booking">
      <h2>Book an Ad</h2>
      
      {/* ✅ Newspaper selection */}
      <label>Select Newspaper:</label>
      <select onChange={handleNewspaperSelect} value={selectedNewspaper}>
        <option value="">-- Select --</option>
        {newspapers.map((np) => (
          <option key={np.id} value={np.id}>{np.name}</option>
        ))}
      </select>

      {/* ✅ Ad space selection */}
      {selectedNewspaper && (
        <div className="ad-spaces">
          <h3>Select Ad Space</h3>
          <div className="newspaper-preview">
            {adSpaces.map((space) => (
              <div
                key={space.id}
                className={`ad-space ${space.status === "booked" ? "booked" : "available"}`}
                onClick={() => handleAdSpaceSelect(space)}
              >
                {space.status === "booked" ? "Booked" : `Price: ₹${space.price}`}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* ✅ Ad content input */}
      {selectedAdSpace && (
        <div className="ad-input">
          <h3>Enter Ad Content</h3>
          <textarea value={adContent} onChange={handleAdContentChange} />
          <p>Price: ₹{calculatedPrice}</p>
          <button onClick={handleBookAd}>Book Ad</button>
        </div>
      )}
    </div>
  );
};

export default AdBooking;
