// src/pages/PaymentPage.js
import React from "react";
import { useLocation } from "react-router-dom";

const PaymentPage = () => {
    const location = useLocation();

    // Log location.state to see what data is being passed
    console.log(location.state);

    // Check if location.state is available
    if (!location.state) {
        return <div>Error: No booking details available. Please return to the booking page.</div>;
    }

    const { newspaperName, adTitle, adContent, duration, totalPrice } = location.state;

    const payNow = () => {
        const options = {
            key: "YOUR_RAZORPAY_KEY", // Replace with your Razorpay key
            amount: totalPrice * 100, // Amount in paise (multiply by 100)
            currency: "INR",
            name: "Your Company Name",
            description: "Payment for Ad Booking",
            handler: function (response) {
                // Handle payment success (e.g., redirect or display a success message)
                console.log(response);
                alert("Payment Successful!");
            },
            prefill: {
                name: "Customer Name", // Replace with actual customer name
                email: "customer@example.com", // Replace with actual customer email
                contact: "9999999999", // Replace with actual customer contact number
            },
            notes: {
                address: "Hello World",
            },
            theme: {
                color: "#F37254",
            },
        };

        const razorpay = new window.Razorpay(options);
        razorpay.open();
    };

    return (
        <div>
            <h1>Complete Your Payment</h1>
            <h2>{newspaperName}</h2>
            <p><strong>Ad Title:</strong> {adTitle}</p>
            <p><strong>Ad Content:</strong> {adContent}</p>
            <p><strong>Duration:</strong> {duration} day(s)</p>
            <p><strong>Total Price:</strong> â‚¹{Price}</p>
            <button onClick={payNow}>Pay Now</button>
        </div>
    );
};

export default PaymentPage;
