import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../../styles/NewspaperLayout.css";

const NewspaperLayout = () => {
  const [selectedAd, setSelectedAd] = useState(null);
  const [selectedPrice, setSelectedPrice] = useState(null);
  const [selectedSize, setSelectedSize] = useState("");
  const [adSpaces, setAdSpaces] = useState([]); // âœ… Initialize adSpaces state
  const navigate = useNavigate();

  const adPrices = {
    large: 5000,
    medium: 3000,
    small: 1500,
  };

  // âœ… Fetch ad spaces from backend
  useEffect(() => {
    const fetchAdSpaces = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/ad-booking/ad-spaces/1"
        );
        setAdSpaces(response.data);
      } catch (error) {
        console.error("Error fetching ad spaces:", error);
      }
    };

    fetchAdSpaces();
  }, []);

  const handleAdClick = (size, index, status) => {
    if (status === "booked") return; // Prevent selecting booked spaces
    setSelectedAd(index);
    setSelectedPrice(adPrices[size]);
    setSelectedSize(size);
  };

  const handleAdBooking = () => {
    navigate("/deccan-chronicles-booking", {
      state: {
        size: selectedSize,
        price: selectedPrice,
        newspaperId: 1,
        newspaperName: "The Virtual Times",
        spaceId: selectedAd + 1,
      },
    });
  };

  return (
    <div className="newspaper-layout">
      <h1>THE VIRTUAL TIMES</h1>
      <div className="ads-container">
        {adSpaces.map((ad, index) => (
          <div
            key={index}
            className={`ad-booking ${ad.size} ${
              ad.status === "booked" ? "disabled" : ""
            }`}
            onClick={() => handleAdClick(ad.size, index, ad.status)}
          >
            ðŸ”¹ <strong>{ad.size?.toUpperCase()} Ad Space</strong> ðŸ”¹
            {ad.status === "booked" && <p>Booked</p>}
            {selectedAd === index && ad.status !== "booked" && (
              <div>
                <p>Price: â‚¹{selectedPrice}</p>
                <button onClick={handleAdBooking}>Book Ad</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default NewspaperLayout;
