import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/BookAd.css";

const PaymentPage = () => {
  const { bookingId } = useParams();
  const [bookingDetails, setBookingDetails] = useState(null);

  useEffect(() => {
    const fetchBookingDetails = async () => {
      try {
        const response = await axios.get(`/api/bookings/${bookingId}`);
        setBookingDetails(response.data);
      } catch (error) {
        console.error("Error fetching booking details: ", error);
      }
    };
    fetchBookingDetails();
  }, [bookingId]);

  const handleVerification = async (status) => {
    try {
      // Update payment approval status
      await axios.post("/api/admin/verify-payment", {
        bookingId,
        approvalStatus: status,
      });

      alert(`Payment ${status} successfully!`);

      if (status === "rejected") {
        // Make the ad space available again if rejected
        await axios.post("/api/admin/update-ad-space", {
          spaceId: bookingDetails.space_id,
          status: "available",
        });
      }

      window.location.href = "/admin-dashboard";
    } catch (error) {
      console.error("Error verifying payment: ", error);
      alert("Failed to verify payment.");
    }
  };

  if (!bookingDetails) return <p>Loading booking details...</p>;

  return (
    <div className="payment-container">
      <h2>Payment Verification</h2>
      <p><strong>Title:</strong> {bookingDetails.title}</p>
      <p><strong>Category:</strong> {bookingDetails.category}</p>
      <p><strong>Duration:</strong> {bookingDetails.duration} day</p>
      <p><strong>Price:</strong> â‚¹{bookingDetails.price}</p>
      <img
        src={`/${bookingDetails.payment_proof}`}
        alt="Payment Proof"
        className="payment-proof"
      />

      <div className="admin-actions">
        <button onClick={() => handleVerification("approved")} className="approve-btn">Approve</button>
        <button onClick={() => handleVerification("rejected")} className="reject-btn">Reject</button>
      </div>
    </div>
  );
};

export default PaymentPage;
