import axios from "axios";

const BASE_URL = "http://localhost:5000/api";

export const login = async (credentials) => {
  try {
    const response = await axios.post(`${BASE_URL}/auth/login`, credentials);
    return response.data;
  } catch (error) {
    console.error("❌ Login Error:", error.response?.data || error);
    return error.response?.data || { message: "Login failed!" };
  }
};

export const getNewspapers = async () => {
  try {
    const response = await axios.get(`${BASE_URL}/newspapers`); // ✅ Fix this line
    return response.data;
  } catch (error) {
    console.error("❌ Fetch Newspapers Error:", error.response?.data || error);
    return [];
  }
};

export const bookAd = async (adData) => {
  try {
    const token = localStorage.getItem("token");
    const response = await axios.post(`${BASE_URL}/ads/book`, adData, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    console.error("❌ Booking Error:", error.response?.data || error);
    throw error;
  }
};
