import React, { useState, useEffect } from "react";
import { getNewspapers } from "../services/api";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [content, setContent] = useState("");
  const [price, setPrice] = useState(0);
  const [loading, setLoading] = useState(true); // ✅ Added loading state

  useEffect(() => {
    const fetchNewspapers = async () => {
      try {
        setLoading(true);
        const data = await getNewspapers();
        console.log("Fetched newspapers:", data); // ✅ Debugging log

        if (Array.isArray(data)) {
          setNewspapers(data);
        } else {
          setNewspapers([]);
        }
      } catch (error) {
        console.error("Error fetching newspapers:", error);
        setNewspapers([]); // Prevent errors
      } finally {
        setLoading(false);
      }
    };

    fetchNewspapers();
  }, []);

  const handleNewspaperChange = (e) => {
    const selectedId = parseInt(e.target.value);
    const newspaper = newspapers.find((paper) => paper.id === selectedId);
    setSelectedNewspaper(newspaper);
    setPrice(0);
    setContent("");
  };

  const handleContentChange = (e) => {
    const text = e.target.value;
    setContent(text);

    if (selectedNewspaper) {
      const wordCount = text.trim().split(/\s+/).length;
      const calculatedPrice = wordCount * selectedNewspaper.price_per_word;
      setPrice(calculatedPrice);
    }
  };

  return (
    <div>
      <h2>Book Your Ad</h2>

      <label>Select a Newspaper:</label>
      <select onChange={handleNewspaperChange}>
        <option value="">Select a newspaper</option>
        {loading ? (
          <option disabled>Loading newspapers...</option> // ✅ Shows proper loading text
        ) : newspapers.length > 0 ? (
          newspapers.map((paper) => (
            <option key={paper.id} value={paper.id}>
              {paper.name} - ₹{paper.price_per_word} per word
            </option>
          ))
        ) : (
          <option disabled>No newspapers available</option> // ✅ Prevents blank dropdown
        )}
      </select>

      {selectedNewspaper && (
        <>
          <div>
            <label>Enter Ad Content:</label>
            <textarea value={content} onChange={handleContentChange} placeholder="Type your ad content here..." />
          </div>
          <p><strong>Total Price: ₹{price}</strong></p>
        </>
      )}
    </div>
  );
};

export default AdBooking;
