import React, { useState } from "react";
import "../styles/NewspaperLayout.css"; // Ensure you have the correct CSS path

const NewspaperLayout = () => {
  const [currentPage, setCurrentPage] = useState(1);

  const ads = [
    { category: "Business Ad", price: "$50 per week" },
    { category: "Matrimony Ad", price: "$40 per week" },
    { category: "Travel Ad", price: "$45 per week" },
    { category: "Property Ad", price: "$60 per week" },
    { category: "Vehicle Ad", price: "$35 per week" },
  ];

  return (
    <div className="newspaper-container">
      <h1 className="newspaper-title">The Times - Advertisement Booking</h1>

      {/* Page Navigation */}
      <div className="page-navigation">
        {[1, 2, 3].map((page) => (
          <button
            key={page}
            className={currentPage === page ? "active" : ""}
            onClick={() => setCurrentPage(page)}
          >
            Page {page}
          </button>
        ))}
      </div>

      {/* Newspaper Layout */}
      <div className="newspaper-content">
        <div className="news-section">
          {currentPage === 1 && (
            <>
              <h2>Best Travel Destinations for 2025</h2>
              <p>Plan your next adventure...</p>
              <h2>Upcoming Movie Releases</h2>
              <p>Exciting films coming soon...</p>
            </>
          )}
          {currentPage === 2 && (
            <>
              <h2>Tech Innovations of the Year</h2>
              <p>Latest breakthroughs in AI, robotics, and more...</p>
              <h2>Sports Highlights</h2>
              <p>Top moments from recent games...</p>
            </>
          )}
          {currentPage === 3 && (
            <>
              <h2>Health & Wellness Trends</h2>
              <p>Best practices for a healthy lifestyle...</p>
              <h2>Real Estate Updates</h2>
              <p>Latest property market insights...</p>
            </>
          )}
        </div>

        {/* Ad Spaces */}
        <div className="ad-section">
          {ads.map((ad, index) => (
            <button key={index} className="ad-box">
              {ad.category} <br /> {ad.price}
            </button>
          ))}
        </div>
      </div>

      {/* Category Selection */}
      <div className="category-selection">
        <label>Select Advertisement Category:</label>
        <select>
          {ads.map((ad, index) => (
            <option key={index} value={ad.category}>
              {ad.category} - {ad.price}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default NewspaperLayout;
