import React, { useEffect, useState } from "react";
import axios from "../services/api";
import "../styles/AdminPanel.css";

const AdminPanel = () => {
  const [pendingBookings, setPendingBookings] = useState([]);

  useEffect(() => {
    fetchPendingBookings();
  }, []);

  const fetchPendingBookings = async () => {
    try {
      const response = await axios.get("/ad-booking/admin/pending-bookings");
      setPendingBookings(response.data);
    } catch (error) {
      console.error("Error fetching pending bookings:", error);
    }
  };

  const handleApprove = async (bookingId) => {
    try {
      await axios.post("/ad-booking/admin/approve", { bookingId });
      alert("Booking approved successfully");
      fetchPendingBookings();
    } catch (error) {
      console.error("Error approving booking:", error);
      alert("Error approving booking");
    }
  };

  const handleReject = async (bookingId, spaceId) => {
    try {
      await axios.post("/ad-booking/admin/reject", { bookingId, spaceId });
      alert("Booking rejected and ad space released");
      fetchPendingBookings();
    } catch (error) {
      console.error("Error rejecting booking:", error);
      alert("Error rejecting booking");
    }
  };

  return (
    <div className="admin-panel">
      <h2>Admin Panel - Pending Bookings</h2>
      {pendingBookings.length === 0 ? (
        <p>No pending bookings</p>
      ) : (
        <div className="booking-list">
          {pendingBookings.map((booking) => (
            <div key={booking.id} className="booking-item">
              <h3>{booking.title}</h3>
              <p><strong>Newspaper:</strong> {booking.newspaper_name}</p>
              <p><strong>Content:</strong> {booking.content}</p>
              <p><strong>Category:</strong> {booking.category}</p>
              <p><strong>Price:</strong> â‚¹{booking.price}</p>
              <button onClick={() => handleApprove(booking.id)}>Approve</button>
              <button onClick={() => handleReject(booking.id, booking.space_id)}>Reject</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
