import React, { useState, useEffect } from "react";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]); // Ensure it's an array
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);
  const [content, setContent] = useState("");
  const [price, setPrice] = useState(0);

  // Fetch newspapers from the backend
  useEffect(() => {
    fetch("http://localhost:5000/api/newspapers")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched Newspapers:", data); // Debugging

        if (Array.isArray(data)) {
          setNewspapers(data);
        } else {
          console.error("Expected an array but got:", data);
          setNewspapers([]); // Ensure it's always an array
        }
      })
      .catch((error) => {
        console.error("Error fetching newspapers:", error);
        setNewspapers([]); // Set as empty array in case of failure
      });
  }, []);

  // Handle newspaper selection
  const handleNewspaperChange = (e) => {
    const selectedId = e.target.value;
    const newspaper = newspapers.find((paper) => paper.id === parseInt(selectedId));
    setSelectedNewspaper(newspaper);
    setPrice(0); // Reset price when a new newspaper is selected
    setContent(""); // Reset content
  };

  // Handle content input and price calculation
  const handleContentChange = (e) => {
    const text = e.target.value;
    setContent(text);

    if (selectedNewspaper) {
      const wordCount = text.trim().split(/\s+/).length; // Count words
    const calculatedPrice = wordCount * selectedNewspaper.price_per_word;

      setPrice(calculatedPrice);
    }
  };

  return (
    <div>
      <h2>Book Your Ad</h2>

      <label>Select a Newspaper:</label>
      <select onChange={handleNewspaperChange}>
        <option value="">Select a newspaper</option>
        {newspapers.length > 0 ? (
          newspapers.map((paper) => (
            <option key={paper.id} value={paper.id}>
              {paper.name} - ₹{paper.pricePerCharacter}/char
            </option>
          ))
        ) : (
          <option disabled>Loading newspapers...</option>
        )}
      </select>

      {selectedNewspaper && (
        <>
          <div>
            <label>Enter Ad Content:</label>
            <textarea value={content} onChange={handleContentChange} placeholder="Type your ad content here..." />
          </div>
          <p><strong>Total Price: ₹{price}</strong></p>
        </>
      )}
    </div>
  );
};

export default AdBooking;
