import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

// âœ… Get token from localStorage
const getToken = () => localStorage.getItem("token");

// âœ… Fetch newspapers
export const getNewspapers = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/newspapers`);
    return response.data;
  } catch (error) {
    console.error("âŒ Fetch Newspapers Error:", error);
    return [];
  }
};

// âœ… Login API
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/login`, {
      email,
      password,
    });

    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
      console.log("âœ… Token saved:", response.data.token);
    } else {
      console.warn("âš ï¸ No token received from the server.");
    }

    return response.data; // Return response data
  } catch (error) {
    console.error("âŒ Login Error:", error.response?.data || error.message);
    throw error; // Rethrow error for handling in components
  }
};

// âœ… Signup API
export const signupUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/signup`, userData);
    return response.data; // Return signup response
  } catch (error) {
    console.error("âŒ Signup Error:", error.response?.data || error.message);
    throw error; // Rethrow error
  }
};

// âœ… Fetch Authenticated User
export const getUserData = async () => {
  const token = getToken();
  console.log("ðŸ” Token being sent:", token);

  if (!token) {
    console.warn("âŒ No token found. User is not logged in.");
    return null;
  }

  try {
    const response = await axios.get(`${API_BASE_URL}/auth/user`, {
      headers: {
        Authorization: `Bearer ${token}`, // âœ… Include token
      },
    });

    console.log("âœ… User Data Response:", response.data);
    return response.data; // Return user data
  } catch (error) {
    console.error("âŒ User Fetch Error:", error.response?.data || error.message);

    if (error.response?.status === 401) {
      console.warn("ðŸš¨ Invalid token detected. Logging out...");
      localStorage.removeItem("token"); // Remove invalid token
    }

    return null; // Return null if unauthorized
  }
};



// âœ… Decode JWT to Get User Info (Alternative)
export const getAuthenticatedUser = () => {
  const token = getToken();
  if (!token) return null;

  try {
    // Decode JWT payload (middle part)
    const payload = JSON.parse(atob(token.split(".")[1]));
    console.log("ðŸ” Decoded Token Payload:", payload);
    return payload;
  } catch (error) {
    console.error("âŒ Token decoding error:", error);
    return null;
  }
};

// âœ… Logout User
export const logoutUser = () => {
  localStorage.removeItem("token");
  console.log("ðŸšª User logged out successfully.");
};
