import axios from "axios";

const API_BASE_URL = "http://localhost:5000/api";

// âœ… Get token from localStorage
const getToken = () => localStorage.getItem("token");

// âœ… Fetch newspapers
export const getNewspapers = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/newspapers`);
    return response.data;
  } catch (error) {
    console.error("âŒ Fetch Newspapers Error:", error);
    return [];
  }
};

// âœ… Login API
export const loginUser = async (email, password) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/login`, { email, password });

    if (response.data.token) {
      localStorage.setItem("token", response.data.token);
      console.log("âœ… Token saved:", response.data.token);
      // Check if token is actually stored
      console.log("ðŸ”Ž Token in localStorage:", localStorage.getItem("token"));
    } else {
      console.warn("âš ï¸ No token received from the server.");
    }

    return response.data;
  } catch (error) {
    console.error("âŒ Login Error:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Signup API
export const signupUser = async (userData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/auth/signup`, userData);
    return response.data;
  } catch (error) {
    console.error("âŒ Signup Error:", error.response?.data || error.message);
    throw error;
  }
};

// âœ… Get Authenticated User (Fixed 401 Unauthorized Error)
export const getUserData = async () => {
  const token = getToken();

  if (!token) {
    console.warn("âŒ No token found in localStorage. User is not logged in.");
    return null; // âœ… Prevent API call if no token
  }

  try {
    const response = await axios.get(`${API_BASE_URL}/auth/user`, {
      headers: {
        Authorization: `Bearer ${token}`, // âœ… Include token
        "Content-Type": "application/json",
      },
    });
    return response.data;
  } catch (error) {
    console.error("âŒ User Fetch Error:", error.response?.data || error);

    if (error.response && error.response.status === 401) {
      localStorage.removeItem("token"); // âœ… Remove invalid token
    }

    return null;
  }
};

// âœ… Decode Token to Get User Info (Alternative)
export const getAuthenticatedUser = () => {
  const token = getToken();
  return token ? JSON.parse(atob(token.split("."))[1]) : null;
};
