import React, { useEffect, useState } from "react";
import axios from "axios";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/newspapers")
      .then((res) => {
        setNewspapers(res.data);
      })
      .catch((error) => {
        console.error("Error fetching newspapers:", error);
      });
  }, []);

  const handleNewspaperSelect = (e) => {
    const newspaperId = e.target.value;
    setSelectedNewspaper(newspaperId);

    if (newspaperId) {
      console.log("Selected Newspaper ID:", newspaperId); // ✅ Now used
    }
  };

  return (
    <div>
      <h2>Book an Ad</h2>
      <label>Select Newspaper:</label>
      <select onChange={handleNewspaperSelect}>
        <option value="">-- Select --</option>
        {newspapers.map((paper) => (
          <option key={paper.id} value={paper.id}>{paper.name}</option>
        ))}
      </select>
    </div>
  );
};

export default AdBooking;
