import React, { useEffect, useState } from "react";
import axios from "axios";

const AdBooking = () => {
  const [newspapers, setNewspapers] = useState([]);
  const [selectedNewspaper, setSelectedNewspaper] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:5000/api/newspapers")
      .then((res) => {
        setNewspapers(res.data);
      })
      .catch((error) => {
        console.error("Error fetching newspapers:", error);
      });
  }, []);

  return (
    <div>
      <h2>Book an Ad</h2>
      <label>Select Newspaper:</label>
      <select onChange={(e) => setSelectedNewspaper(e.target.value)}>
        <option value="">-- Select --</option>
        {newspapers.map((paper) => (
          <option key={paper.id} value={paper.id}>{paper.name}</option>
        ))}
      </select>
    </div>
  );
};

export default AdBooking;
